import React, { useState } from 'react';
import { Select,Button, Input } from 'antd';
import {AtomicBlockUtils, DraftHandleValue,EditorState,RichUtils} from 'draft-js';
import Editor from "@draft-js-plugins/editor";
import createImagePlugin from '@draft-js-plugins/image';
import createVideoPlugin from '@draft-js-plugins/video';
import createUndoPlugin from '@draft-js-plugins/undo';

import './richText.css';
import "draft-js/dist/Draft.css"
import Modal from 'antd/lib/modal/Modal';

const {Option} = Select;

const imagePlugin = createImagePlugin();
const videoPlugin = createVideoPlugin();
const undoPlugin = createUndoPlugin();
const { UndoButton, RedoButton } = undoPlugin;
const plugins = [imagePlugin,undoPlugin,videoPlugin];

const RichText = ()=>{
    const [editorSta, setEditorSta] = useState(()=>EditorState.createEmpty());
    const [isVedioModalVisible, setIsVedioModalVisible] = useState(false);
    const [vedioSrc, setVedioSrc] = useState("");


    
    function onEditorChange(val){
        setEditorSta(val);
    }

    //加粗
    function _onBoldClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'BOLD'));
    }
    //斜体
    function _onItalicClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'ITALIC'));
    }
    //下划线
    function _onUnderlineClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'UNDERLINE'));
    }
    //H1
    function _onH1Click(){
        onEditorChange(RichUtils.toggleBlockType(editorSta,'header-one'));
    }
    //引用块
    function _onBlockQuoteClick(){
        onEditorChange(RichUtils.toggleBlockType(editorSta,'blockquote'));
    }
    //code块
    function _onCodeBlockClick(){
        onEditorChange(RichUtils.toggleBlockType(editorSta,'code-block'));
    }
    //无序列表
    function _onUnOrderClick(){
        onEditorChange(RichUtils.toggleBlockType(editorSta,'unordered-list-item'));
    }
    //有序列表
    function _onOrderClick(){
        onEditorChange(RichUtils.toggleBlockType(editorSta,'ordered-list-item'));
    }
    //红色
    function _onRedClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'red'));
    }
    //蓝色
    function _onBlueClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'blue'));
    }
    
    //左对齐
    function _onLeftClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'left'));
    }
    //右对齐
    function _onRightClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'right'));
    }
    //居中
    function _onCenterClick(){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,'center'));
    }
    function insertImage (editorState, base64) {
        const contentState = editorState.getCurrentContent();
        const contentStateWithEntity = contentState.createEntity(
          "image",
          "IMMUTABLE",
          { src: base64 }
        );
        const entityKey = contentStateWithEntity.getLastCreatedEntityKey();
        const newEditorState = EditorState.set(editorState, {
          currentContent: contentStateWithEntity
        });
        return AtomicBlockUtils.insertAtomicBlock(newEditorState, entityKey, " ");
    };
    
    function _onImgClick(){
      const base64 = "data:image/png;base64,";
    //   const base64 = "data:image/jpeg;base64,";
      const newEditorState = insertImage(editorSta, base64);
      setEditorSta(newEditorState);
    }

    function _onVedioClick(){
        setEditorSta(videoPlugin.addVideo(editorSta,{src:"https://youtu.be/3PazbAYoHK8"}))
    }
    // function _vedioModalOk(){
    //     setVedioSrc();
    // }
    // function _vedioModalCancel(){
        
    // }
    // function _vedioInp(e){
    //     setVedioSrc(e.target.value);
    // }
    //行内样式匹配
    const styleMap = {
        '10px':{
            fontSize: 10
        },
        '20px':{
            fontSize: 20
        },
        '30px':{
            fontSize: 30
        },
        '40px':{
            fontSize: 40
        },
        'red':{
            color: '#d60808'
        },
        'blue':{
            color: '#2B60DD'
        }
    };
    //代码块
    function _onSizeClick(value){
        onEditorChange(RichUtils.toggleInlineStyle(editorSta,value));
    }
    

    //键位绑定
    const handleKeyCommand = (
        command: string,
        editorState: EditorState
    ): DraftHandleValue => {
        const newState = RichUtils.handleKeyCommand(editorState, command);

        if (newState) {
            onEditorChange(newState);
            return "handled";
        }

        return "not-handled";
    };

    function _showMedia(type) {
        this.setState({
            showURLInput: true,
            urlValue: '',
            urlType: type,
        }, () => {
            setTimeout(() => this.refs.url.focus(), 0);
        });
    }

    return (
        <div className="editor-container">
            <div className="header">
                <Button type="primary" shape="circle" onClick={_onBoldClick}>B</Button>
                <Button type="primary" shape="circle" onClick={_onItalicClick}>I</Button>
                <Button type="primary" shape="circle" onClick={_onUnderlineClick}>U</Button>
                <Button type="primary" shape="circle" onClick={_onH1Click}>H1</Button>
                <Button type="primary" shape="circle" onClick={_onBlockQuoteClick}>“”</Button>
                <Button type="primary" shape="circle" onClick={_onCodeBlockClick}>C</Button>
                <Button type="primary" shape="circle" onClick={_onUnOrderClick}>无</Button>
                <Button type="primary" shape="circle" onClick={_onOrderClick}>有</Button>
                <Button type="primary" shape="circle" onClick={_onImgClick}>图</Button>
                <Button type="primary" shape="circle" onClick={_onVedioClick}>视</Button>
                <Button type="primary" shape="circle" onClick={_onRedClick}>红</Button>
                <Button type="primary" shape="circle" onClick={_onBlueClick}>蓝</Button>
                {/* <Button type="primary" shape="circle" onClick={_onLeftClick}>左</Button>
                <Button type="primary" shape="circle" onClick={_onRightClick}>右</Button>
                <Button type="primary" shape="circle" onClick={_onCenterClick}>中</Button> */}
                <Select style={{ width: "100px" }} placeholder="选择大小" onChange={_onSizeClick}>
                    <Option value="10px">10px</Option>
                    <Option value="20px">20px</Option>
                    <Option value="30px">30px</Option>
                    <Option value="40px">40px</Option>
                </Select>
                <UndoButton />
                <RedoButton />
            </div>
            
            <div className="editor-input">
            <Editor
                customStyleMap={styleMap}
                editorState={editorSta}
                onChange={onEditorChange} 
                handleKeyCommand={handleKeyCommand}
                plugins={plugins}
            />  
            </div>
            {/* <Modal title="Basic Modal" visible={isVedioModalVisible} onOk={_vedioModalOk} onCancel={_vedioModalCancel}>
                <Input id="vedioSrc" onChange={_vedioInp}/>
            </Modal> */}
        </div>
    );
}
export default RichText;